// src/components/Navbar/Navbar.tsx
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext, AuthContextType } from '../../contexts/AuthContext'; //
import Botao from '../Botao/botao'; //
import navStyles from './Navbar.module.css';

interface NavbarProps {
  isPublic?: boolean;
}

const Navbar: React.FC<NavbarProps> = ({ isPublic = false }) => {
  const authContextData = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    if (authContextData && authContextData.logout) {
      authContextData.logout();
    }
    navigate('/login');
  };

  const isAuthenticated = authContextData?.isAuthenticated || false;

  // Define as classes condicionalmente
  const headerClass = isPublic ? `${navStyles.navbarHeader} ${navStyles.navbarHeaderPublic}` : navStyles.navbarHeader;
  const brandClass = isPublic ? `${navStyles.navbarBrand} ${navStyles.navbarBrandPublic}` : navStyles.navbarBrand;
  const linksClass = isPublic ? `${navStyles.navbarLinks} ${navStyles.navbarLinksPublic}` : navStyles.navbarLinks;
  const logoutButtonClass = isPublic ? `${navStyles.navbarLogoutButton} ${navStyles.navbarLogoutButtonPublic}` : navStyles.navbarLogoutButton;


  return (
    <header className={headerClass}>
      <nav className={navStyles.navbarNav}> {/* Pode não precisar de modificador se o conteúdo interno encolher */}
        <h1 className={brandClass}>
          <Link to={isPublic && !isAuthenticated ? "/" : "/dashboard"}>TrackZ</Link>
        </h1>
        {!isPublic && isAuthenticated && (
          <ul className={linksClass}>
            <li><Link to="/dashboard">Feed</Link></li>
            <li><Link to="/perfil">Perfil</Link></li>
            <li><Link to="/explorar">Explorar</Link></li>
            <li><Link to="/configuracoes">Configurações</Link></li>
            <li>
              <Botao onClick={handleLogout} className={logoutButtonClass}>
                Logout
              </Botao>
            </li>
          </ul>
        )}
        {isPublic && (
          <ul className={linksClass}>
            {!isAuthenticated && window.location.pathname === "/login" && (
                <li><Link to="/cadastro">Cadastre-se</Link></li>
            )}
            {!isAuthenticated && window.location.pathname === "/cadastro" && (
                <li><Link to="/login">Login</Link></li>
            )}
            {/* Se o usuário estiver logado e em uma página pública (improvável com RotaPrivada),
                você poderia adicionar um link para o Dashboard.
                Mas vamos manter simples para telas de login/cadastro.
            */}
          </ul>
        )}
      </nav>
    </header>
  );
};

export default Navbar;
