// src/components/Navbar/Navbar.tsx
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext, AuthContextType } from '../../contexts/AuthContext'; //
import Botao from '../Botao/botao'; //
import navStyles from './Navbar.module.css';

interface NavbarProps {
  isPublic?: boolean; // Nova prop opcional
}

const Navbar: React.FC<NavbarProps> = ({ isPublic = false }) => {
  // useContext só é relevante se não for público, ou para pegar 'isAuthenticated'
  const authContextData = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    if (authContextData && authContextData.logout) {
      authContextData.logout();
    }
    navigate('/login');
  };

  // Extrai isAuthenticated com segurança, mesmo que authContextData seja null (se o contexto não for encontrado)
  const isAuthenticated = authContextData?.isAuthenticated || false;

  return (
    <header className={navStyles.navbarHeader}>
      <nav className={navStyles.navbarNav}>
        <h1 className={navStyles.navbarBrand}>
          {/* Se for pública e o usuário já estiver logado, o link pode ir pro dashboard, senão pra home */}
          <Link to={isPublic ? "/" : "/dashboard"}>TrackZ</Link>
        </h1>
        {!isPublic && isAuthenticated && ( // Links do usuário logado
          <ul className={navStyles.navbarLinks}>
            <li><Link to="/dashboard">Feed</Link></li>
            <li><Link to="/perfil">Perfil</Link></li>
            <li><Link to="/explorar">Explorar</Link></li>
            <li><Link to="/configuracoes">Configurações</Link></li>
            <li>
              <Botao onClick={handleLogout} className={navStyles.navbarLogoutButton}>
                Logout
              </Botao>
            </li>
          </ul>
        )}
        {isPublic && ( // Opções para navbar pública (ex: tela de login/cadastro)
          <ul className={navStyles.navbarLinks}>
            {/* Adicione aqui links relevantes para uma navbar pública, se houver */}
            {/* Exemplo: <li><Link to="/sobre">Sobre</Link></li> */}
            {/* Se não houver links, você pode omitir este <ul> ou deixar vazio */}
            {!isAuthenticated && <li><Link to={window.location.pathname === "/login" ? "/cadastro" : "/login"}>{window.location.pathname === "/login" ? "Cadastre-se" : "Login"}</Link></li>}
          </ul>
        )}
      </nav>
    </header>
  );
};

export default Navbar;
