import React, { useState, ChangeEvent, FormEvent } from "react";
import styles from "./FormularioCadastro.module.css";
import Botao from "../Botao/botao";

// Você já tem suas interfaces de estado e erro, o que é ótimo.
// Se não tivesse, seria:
// interface IErros {
//   nome?: string;
//   email?: string;
//   usuario?: string;
//   senha?: string;
//   confirmarSenha?: string;
//   geral?: string;
// }

const FormularioCadastro: React.FC = () => { // Adicionado : React.FC para tipagem do componente
    const [nome, setNome] = useState("");
    const [email, setEmail] = useState("");
    const [usuario, setUsuario] = useState("");
    const [senha, setSenha] = useState("");
    const [confirmarSenha, setConfirmarSenha] = useState("");
    const [erros, setErros] = useState<{ [campo: string]: string }>({}); // Mantendo sua tipagem de erros

    const validarCampos = () => { // Função validarCampos corrigida
        const novosErros: { [campo: string]: string } = {};

        if (!nome.trim()) novosErros.nome = "Nome completo é obrigatório.";
        if (!email.trim()) {
            novosErros.email = "E-mail é obrigatório.";
        } else if (!email.includes('@') || !email.includes('.')) { // Validação de email simples
            novosErros.email = 'E-mail inválido.';
        }
        if (!usuario.trim()) novosErros.usuario = "Nome de usuário é obrigatório.";
        if (!senha) {
            novosErros.senha = 'Senha é obrigatória.';
        } else if (senha.length < 6) {
            novosErros.senha = 'Senha precisa ter pelo menos 6 caracteres.';
        }
        if (!confirmarSenha) {
            novosErros.confirmarSenha = 'Confirmação de senha é obrigatória.';
        } else if (senha !== confirmarSenha) {
            novosErros.confirmarSenha = 'Senhas não coincidem.';
        }

        setErros(novosErros);
        return Object.keys(novosErros).length === 0;
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!validarCampos()) { // Descomentado
            // alert("Por favor, corrija os erros no formulário."); // Opcional, os erros já aparecem
            return;
        }
        console.log('Cadastro enviado:', { nome, email, usuario, senha });
        // Simulação de submissão
        setTimeout(() => {
            alert(`Usuário ${usuario} cadastrado com sucesso!`);
            // Limpar formulário
            setNome('');
            setEmail('');
            setUsuario('');
            setSenha('');
            setConfirmarSenha('');
            setErros({});
            // Aqui você pode simular navegação também
        }, 1000);
    };

    return (
        <form className={styles.form} onSubmit={handleSubmit}>
            {/* Campo Nome Completo */}
            <div className={styles.campo}>
                <label className={styles.label} htmlFor="cadastro-nome"> {/* Adicionado htmlFor */}
                    Nome Completo:
                    <input
                        type="text"
                        id="cadastro-nome" 
                        name="nome" // Adicionado name para consistência se validarCampos usar
                        value={nome}
                        onChange={(e) => setNome(e.target.value)}
                        className={styles.input}
                        required
                        placeholder="Digite seu nome completo"
                        aria-invalid={!!erros.nome}
                        aria-describedby={erros.nome ? "nome-erro" : undefined}
                    />
                    {erros.nome && <span id="nome-erro" className={styles.error}>{erros.nome}</span>}
                </label>
            </div>

            {/* Campo Email */}
            <div className={styles.campo}> {/* ADICIONADA A DIV .campo */}
                <label className={styles.label} htmlFor="cadastro-email"> {/* ADICIONADA CLASSE E htmlFor */}
                    Email:
                    <input
                        type="email" // CORRIGIDO TYPE
                        id="cadastro-email"
                        name="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className={styles.input}
                        required
                        placeholder="Digite um email válido"
                        aria-invalid={!!erros.email}
                        aria-describedby={erros.email ? "email-erro" : undefined}
                    />
                    {erros.email && <span id="email-erro" className={styles.error}>{erros.email}</span>}
                </label>
            </div>

            {/* Campo Nome de Usuário */}
            <div className={styles.campo}> {/* ADICIONADA A DIV .campo */}
                <label className={styles.label} htmlFor="cadastro-usuario"> {/* ADICIONADA CLASSE E htmlFor */}
                    Nome de Usuário:
                    <input
                        type="text"
                        id="cadastro-usuario"
                        name="usuario"
                        value={usuario}
                        onChange={(e) => setUsuario(e.target.value)}
                        className={styles.input}
                        required
                        placeholder="Digite um nome de usuário"
                        aria-invalid={!!erros.usuario}
                        aria-describedby={erros.usuario ? "usuario-erro" : undefined}
                    />
                    {erros.usuario && <span id="usuario-erro" className={styles.error}>{erros.usuario}</span>}
                </label>
            </div>

            {/* Campo Senha */}
            <div className={styles.campo}> {/* ADICIONADA A DIV .campo */}
                <label className={styles.label} htmlFor="cadastro-senha"> {/* ADICIONADA CLASSE E htmlFor */}
                    Senha:
                    <input
                        type="password"
                        id="cadastro-senha"
                        name="senha"
                        value={senha}
                        onChange={(e) => setSenha(e.target.value)}
                        className={styles.input}
                        required
                        placeholder="Digite uma senha (mín. 6 caracteres)"
                        aria-invalid={!!erros.senha}
                        aria-describedby={erros.senha ? "senha-erro" : undefined}
                    />
                    {erros.senha && <span id="senha-erro" className={styles.error}>{erros.senha}</span>}
                </label>
            </div>
            
            {/* Campo Confirmar Senha */}
            <div className={styles.campo}> {/* ADICIONADA A DIV .campo */}
                <label className={styles.label} htmlFor="cadastro-confirmar-senha"> {/* ADICIONADA CLASSE E htmlFor */}
                    Confirmar Senha:
                    <input
                        type="password"
                        id="cadastro-confirmar-senha"
                        name="confirmarSenha"
                        value={confirmarSenha}
                        onChange={(e) => setConfirmarSenha(e.target.value)}
                        className={styles.input}
                        required
                        placeholder="Confirme a senha digitada"
                        aria-invalid={!!erros.confirmarSenha}
                        aria-describedby={erros.confirmarSenha ? "confirmar-senha-erro" : undefined}
                    />
                    {erros.confirmarSenha && <span id="confirmar-senha-erro" className={styles.error}>{erros.confirmarSenha}</span>}   
                </label>
            </div>
            <Botao type="submit"> Cadastrar </Botao>
        </form>
    )
}
export default FormularioCadastro;
