// login.tsx
import React from 'react';
import Navbar from '../../components/Navbar/Navbar';
import FormularioLogin from '../../components/FormularioLogin/FormularioLogin'; //
import styles from './login.module.css';
import { Link } from 'react-router-dom';

const Login: React.FC = () => {
  return (
    // Este 'div' agora é o container principal que ocupa 100vh
    // e organiza a Navbar e o conteúdo interno.
    <div className={styles.pageWrapper}> {/* Nova classe wrapper */}
      <Navbar isPublic={true} />
      {/* Este container agora é para o conteúdo específico da página, centralizado */}
      <main className={styles.contentContainer}>
        <h1 className={styles.header}>Login</h1>
        <div className={styles.loginFormContainer}>
          <FormularioLogin />
        </div>
        <p className={styles.linkCadastro}>
          Não tem uma conta? <Link to="/cadastro">Cadastre-se</Link>
        </p>
      </main>
      {/* Você pode adicionar um footer aqui se quiser, dentro de pageWrapper */}
    </div>
  );
};

export default Login;
